import React from "react";

function ShopItem({ img, name, cost, views }) {
  const toShopDetail = () => (
    <Link className='shop__link' to='/shopDetail'>
      <ShopDetail
        key={id}
        img={itemImg}
        name={itemName}
        cost={itemCost}
        location='calgary'
        description='Nice'
        views={itemViews}
      />
    </Link>
  );
  console.log(img, name, cost, views);
  return (
    <div className='item' onClick={toShopDetail}>
      <img src={img} alt='' />
      <p>{name}</p>
      <p>${cost}</p>
      <p>{views}</p>
    </div>
  );
}

export default ShopItem;
