import React from "react";
import { Link } from "react-router-dom";
import ShopDetail from "../shopDetail/ShopDetail";

function ShopItem({ id, img, name, cost, views }) {
  // const toShopDetail = () => (
  // <Link className='shop__link' to='/shopDetail'>
  //   <ShopDetail
  //     key={id}
  //     img={img}
  //     name={name}
  //     cost={cost}
  //     location='calgary'
  //     description='Nice'
  //     views={views}
  //   />
  // </Link>
  // );
  console.log(img, name, cost, views);
  return (
    <div
      className='item'
      onClick={
        <Link className='shop__link' to='/shopDetail'>
          <ShopDetail
            key={id}
            img={img}
            name={name}
            cost={cost}
            location='calgary'
            description='Nice'
            views={views}
          />
        </Link>
      }>
      <img src={img} alt='' />
      <p>{name}</p>
      <p>${cost}</p>
      <p>{views}</p>
    </div>
  );
}

export default ShopItem;
