import React, { useState, useEffect } from "react";
import "./Shop.css";
import Header from "../header/Header";
import { Link } from "react-router-dom";
import ShopItem from "./ShopItem";
import { db } from "../../firebase";
import ShopDetail from "../shopDetail/ShopDetail";

function Shop() {
  const [img, setImg] = useState("");
  const [name, setName] = useState("");
  const [cost, setCost] = useState("");
  const [views, setViews] = useState(0);
  const [details, setDetails] = useState();
  const [items, setItems] = useState([]);
  const [toShopDetail, setToShopDetail] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const selectItem = (items) => {
    setSelectedItem(items);
  };

  useEffect(() => {
    // setItemImg(
    //   "https://specials-images.forbesimg.com/imageserve/5d3703e2f1176b00089761a6/960x0.jpg?cropX1=836&cropX2=5396&cropY1=799&cropY2=3364"
    // );
    // setItemName("Laptop");
    // setItemCost(500);
    // setItemViews(200);
    db.collection("items").onSnapshot((snapshot) =>
      setItems(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data(),
        }))
      )
    );
  }, []);

  // const onClickItem = (e) => {
  //   console.log(toShopDetail);
  //   e.preventDefault();
  //   setToShopDetail(true);
  //   setItemName("");
  // };

  return (
    <>
      <div className='container'>
        <Header />
        {toShopDetail ? (
          <ShopDetail
            // key={id}
            img={itemImg}
            name={itemImg}
            cost={itemCost}
            views={itemViews}
            location='calgary'
            description='Nice'
          />
        ) : (
          <div className='shop__main'>
            <Link to='/uploadItem'>
              <button className='btn_post'>Post Ad</button>
            </Link>
            <div className='item'>
              {/* {selectedItem && (
              <div>
                <ShopDetail
                  img={itemImg}
                  name={itemName}
                  cost={itemCost}
                  location='calgary'
                  description='Nice'
                  views={itemViews}
                />
              </div>
            )} */}

              {items.map(({ id, data: { itemName, itemImg, itemViews, itemCost } }) => (
                <>
                  <div
                    className='item'
                    onClick={(e) => {
                      console.log(toShopDetail);
                      e.preventDefault();
                      setToShopDetail(true);
                      setItemName("");
                    }}>
                    <img src={itemImg} alt='' />
                    <p>{itemName}</p>
                    <p>${itemCost}</p>
                    <p>{itemViews}</p>
                  </div>
                </>
              ))}
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Shop;
