  //             <div className='manageItem__list__item'>
  //               <div className='manageItem__list__item__left'>
  //                 <img src={itemImg} alt='' />
  //                 <div className='manageItem__list__item__info'>
  //                   <p>{itemName}</p>
  //                   <p>${itemCost}</p>
  //                 </div>
  //               </div>
  //               <div className='manageItem__list__item__right'>{/* <p>{date.toDate()}</p> */}</div>
  //             </div>
  //           </li>)
  //         } else {
  //             return (<div></div>)
  //         }
  //     }
  //   )}
  //   )}
  //
  //   useEffect(() => {
  //     let allowUser;
  //    if(userId) {
  //        allowUser = db
  //        .collectio("       .collection()"Ite"
  //
  //    }
  //   })
  return (
    <div className='container'>
      <Header />
      <div className='manageItem'>
        <h2> Manage Post</h2>
        {items.map(({ data: { itemName, itemImg, itemCost, userId } }) => (
          <li className='manageItem__list'>
            <input type='checkbox' name='check' />
            <div className='manageItem__list__item'>
              <div className='manageItem__list__item__left'>
                <img src={itemImg} alt='' />
                <div className='manageItem__list__item__info'>
                  <p>{itemName}</p>
                  <p>${itemCost}</p>
                </div>
              </div>

              <div className='manageItem__list__item__right'></div>
            </div>
          </li>
        ))}
      </div>
    </div>
  );
};

export default ManageItem;
