import React, { useState, useEffect } from "react";
import "./Shop.css";
import Header from "../header/Header";
import { Link, Redirect, Route } from "react-router-dom";
import ShopItem from "./ShopItem";
import { db } from "../../firebase";
import ShopDetail from "../shopDetail/ShopDetail";

const Shop = ({ shopDetail }) => {
  const [img, setImg] = useState("");
  const [name, setName] = useState("");
  const [cost, setCost] = useState("");
  const [views, setViews] = useState(0);
  const [details, setDetails] = useState();
  const [items, setItems] = useState([]);
  const [toShopDetail, setToShopDetail] = useState(shopDetail);
  const [selectedItem, setSelectedItem] = useState(null);
  const selectItem = (items) => {
    setSelectedItem(items);
  };

  useEffect(() => {
    // setItemImg(
    //   "https://specials-images.forbesimg.com/imageserve/5d3703e2f1176b00089761a6/960x0.jpg?cropX1=836&cropX2=5396&cropY1=799&cropY2=3364"
    // );
    // setItemName("Laptop");
    // setItemCost(500);
    // setItemViews(200);
    db.collection("items").onSnapshot((snapshot) =>
      setItems(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data(),
        }))
      )
    );
  }, []);

  // const onClickItem = (e) => {
  //   console.log(toShopDetail);
  //   e.preventDefault();
  //   setToShopDetail(true);
  //   setItemName("");
  // };

  return (
    <>
      {toShopDetail ? (
        <>
          <ShopDetail
            // key={id}
            selectedItem
            img={img}
            name={name}
            cost={cost}
            views={views}
            location='calgary'
            description='Nice'
            shopDetail={toShopDetail}
          />
        </>
      ) : (
        // </Redirect>
        <div className='container'>
          <Header />
          <div className='shop__main'>
            <Link to='/uploadItem'>
              <button className='btn_post'>Post Ad</button>
            </Link>
            <div className='item'>
              {/* {selectedItem && (
              <div>
                <ShopDetail
                  img={itemImg}
                  name={itemName}
                  cost={itemCost}
                  location='calgary'
                  description='Nice'
                  views={itemViews}
                />
              </div>
            )} */}

              {items.map(({ id, data: { itemName, itemImg, itemViews, itemCost } }) => (
                <>
                  <Link to='/shopDetail'>
                    <div
                      className='item'
                      onClick={(e) => {
                        console.log(toShopDetail);
                        console.log(itemName);
                        e.preventDefault();
                        setToShopDetail(true);
                        setName(itemName);
                        setImg(itemImg);
                        setViews(itemViews);
                        setCost(itemCost);
                      }}>
                      <img src={itemImg} alt='' />
                      <p>{itemName}</p>
                      <p>${itemCost}</p>
                      <p>{itemViews}</p>
                    </div>
                  </Link>
                </>
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Shop;
