import React, { useEffect, useState } from "react";
import Header from "../components/header/Header";
import CreateIcon from "@material-ui/icons/Create";
import "./Feed.css";
import FeedInputOption from "./FeedInputOption";
import ImageIcon from "@material-ui/icons/Image";
import SubscriptionsIcon from "@material-ui/icons/Subscriptions";
import EventNoteIcon from "@material-ui/icons/EventNote";
import CalendarViewDayIcon from "@material-ui/icons/CalendarViewDay";
import Post from "./Post";
import firebase from "firebase";
import { db } from "./firebase";
// import { useSelector } from "react-redux";
// import { selectUser } from "./features/userSlice";

function Feed({ userId }) {
  const [input, setInput] = useState("");
  const [posts, setPosts] = useState([]);
  // const user = useSelector(selectUser);
  // console.log(user);

  useEffect(() => {
    db.collection("newPosts")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) => {
        setPosts(
          snapshot.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        );
      });
  }, []);

  const onSubmit = (e) => {
    e.preventDefault();
    db.collection("newPosts").add({
      name: user.displayName,
      description: user.email,
      message: input,
      photoUrl: user.photoUrl || "",
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
    });

    setInput("");
  };
  return (
    <>
      <Header />
      <div className='feed__container'>
        <div className='feed__top'>
          <form onSubmit={onSubmit}>
            <CreateIcon className='icon' />
            <input type='text' value={input} onChange={(e) => setInput(e.target.value)} />
            {/* <button type='submit' onClick={onSubmit}>
              send
            </button> */}
          </form>
        </div>
        <div className='feed__bottom'>
          <div className=''>
            <FeedInputOption Icon={ImageIcon} name='Photo' color='#70B5F9' />
          </div>
          <FeedInputOption Icon={SubscriptionsIcon} name='Video' color='#E7A33E' />
          <FeedInputOption Icon={EventNoteIcon} name='Event' color='#C0CBCD' />
          <FeedInputOption Icon={CalendarViewDayIcon} name='Write article' color='#7FC15E' />
        </div>
      </div>
      {posts.map(({ id, data: { description, message, name, photoUrl, timestamp } }) => (
        <Post
          key={id}
          description={description}
          message={message}
          name={name}
          photoUrl={photoUrl}
          timestamp={timestamp}
        />
      ))}
    </>
  );
}

export default Feed;
