import React, { useState, useEffect } from "react";
import "./Shop.css";
import Header from "../header/Header";
import { Link } from "react-router-dom";
import ShopItem from "./ShopItem";
import { db } from "../../firebase";
import ShopDetail from "../shopDetail/ShopDetail";

function Shop() {
  const [img, setImg] = useState("");
  const [name, setName] = useState("");
  const [cost, setCost] = useState("");
  const [views, setViews] = useState(0);
  const [items, setItems] = useState([]);
  const [toShopDetail, setToShopDetail] = useState(false);

  useEffect(() => {
    // setItemImg(
    //   "https://specials-images.forbesimg.com/imageserve/5d3703e2f1176b00089761a6/960x0.jpg?cropX1=836&cropX2=5396&cropY1=799&cropY2=3364"
    // );
    // setItemName("Laptop");
    // setItemCost(500);
    // setItemViews(200);
    db.collection("items").onSnapshot((snapshot) =>
      setItems(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data(),
        }))
      )
    );
  }, []);

  // const onClickItem = (e) => {
  //   console.log(toShopDetail);
  //   e.preventDefault();
  //   setToShopDetail(true);
  // };
  return (
    <>
      <div className='container'>
        <Header />
        <div className='shop_button'>
          <Link to='/uploadItem'>
            <button className='btn_post'>Post Ad</button>
          </Link>
        </div>
        <div className='shop__main'>
          {items.map(({ id, data: { itemName, itemImg, itemViews, itemCost } }) => (
            <div
              className='item'
              onClick={(e) => {
                e.preventDefault();
                setToShopDetail(true);
                setName(itemName);
                setImg(itemImg);
                setViews(itemViews);
                setCost(itemCost);
              }}>
              <img src={itemImg} alt='' />
              <p>{itemName}</p>
              <p>${itemCost}</p>
              <p>{itemViews}</p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Shop;
